#include <openssl/evp.h>
#include <openssl/rand.h>

#include <stdio.h>
#include <string.h>
#include <time.h>
#include <unistd.h>

#include "test_common.h"

unsigned char aes_iv[IV_LENGTH];

int encrypt(FILE *out_file, const unsigned char *key, FILE* error_stream){
	EVP_CIPHER_CTX *ctx = NULL;

	unsigned char auth_tag[AUTH_TAG_LENGTH];

	const size_t BLOCK_SIZE = 16;

	ctx = EVP_CIPHER_CTX_new();
	int ok = EVP_EncryptInit(ctx, EVP_aes_256_gcm(), key, aes_iv);
	
	fwrite(aes_iv, 1, IV_LENGTH, out_file);

    /*
    unsigned char plaintext[] = "";
	size_t plaintext_len = strlen((char *) plaintext);
    */

    size_t plaintext_len = 0;
    unsigned char plaintext[P_BUFFER_SIZE];
    memset(plaintext, 0x00, P_BUFFER_SIZE);
    f_read_contents(PLAINTEXT_FILE, plaintext, P_BUFFER_SIZE);
	plaintext_len = strlen((char *) plaintext);
	unsigned char* out_buf = malloc(plaintext_len + BLOCK_SIZE);

	struct timespec start, end;
	double elapsed_time;

	clock_gettime(CLOCK_MONOTONIC, &start);
	for(int repeat = 0; repeat < LOOP_COUNT; repeat++){
		int out_nbytes = 0;
		EVP_EncryptUpdate(ctx, out_buf, &out_nbytes, plaintext, plaintext_len);
		fwrite(out_buf, 1, out_nbytes, out_file);
		usleep(SLEEP_INTERVAL);
	}

	clock_gettime(CLOCK_MONOTONIC, &end);

	elapsed_time = (end.tv_sec - start.tv_sec) + (end.tv_nsec - start.tv_nsec) / 1.0e9;

	printf("Execution time: %.6f seconds\n", elapsed_time);

	int out_nbytes = 0;
	EVP_EncryptFinal(ctx, out_buf, &out_nbytes);
	fwrite(out_buf, 1, out_nbytes, out_file);

	ok = EVP_CIPHER_CTX_ctrl(ctx, EVP_CTRL_GCM_GET_TAG, AUTH_TAG_LENGTH, auth_tag);
	fwrite(auth_tag, 1, AUTH_TAG_LENGTH, out_file);

	if(ferror(out_file)){
		if(error_stream)
				fprintf(error_stream, "I/O error\n");
		goto failure;
	}

	if(!ok){
		if(error_stream)
				fprintf(error_stream, "Encryption error\n");
		goto failure;
	}

	goto cleanup;

failure:
	return -1;
cleanup:
	EVP_CIPHER_CTX_free(ctx);
	free(out_buf);

	return 0;
}

int main(int argc, char **argv){
	int exit_code = 0;

	FILE* out_file = NULL;
	unsigned char* key = NULL;

	if(argc != 3){
		fprintf(stderr, "Usage: %s KEU_HEX\n", argv[0]);
		exit_code = 1;
		goto cleanup;
	}

	const char* key_hex = argv[1];

	long decoded_key_len = 0;
	key = OPENSSL_hexstr2buf(key_hex, &decoded_key_len);
	if(!key || decoded_key_len != KEY_LENGTH){
		fprintf(stderr, "Wrong key \"%s\", must be %u hex digites\n", key_hex, KEY_LENGTH * 2);
		goto failure;
	}

	unsigned char *iv = OPENSSL_hexstr2buf(argv[2], &decoded_key_len);
	if(!iv || decoded_key_len != IV_LENGTH){
		fprintf(stderr, "Invalid AES iv! Must be %d bytes (64 hex chars).\n", IV_LENGTH);
		exit(EXIT_FAILURE);
	}
	memcpy(aes_iv, iv, IV_LENGTH);
	OPENSSL_free(iv);

	out_file = fopen("encrypt.norm", "wb");
	encrypt(out_file, key, stderr);

failure:
	exit_code = 1;
cleanup:
	OPENSSL_free(key);
	if(out_file)
		fclose(out_file);

	return exit_code;

}




